<script>
// // @ is an alias to /src
// import anime from "animejs";
import TransitionMixin from '../mixins/transition';

export default {
  name: "Home",
  mixins : [TransitionMixin],
  data() {
    return {
      toggle: false,
      counter: 1,
      isBoxSupport : true
    };
  },
  methods : {
    emit() {
      this.pageTransition();
      this.$router.push({path : '/about'})
    }
  },
  
  mounted() {

    this.$store.state.hover.push(this.$refs.btn);
    
    if (window.getComputedStyle(document.body).transformBox !== undefined) {
      this.isBoxSupport = true
    } else {
      this.isBoxSupport = false
    }

    


    
    const img = document.querySelectorAll(".image svg");
    // console.log(img[0].clientWidth);
    const slide = document.querySelector(".image");
    let size = img[0].clientWidth;
    // console.log(size);
    let counter = 1;
    let isForward = true;
    const eyes = document.querySelectorAll(".pupil");
    document.onmousemove = function () {
      var x = (event.clientX * 100) / window.innerWidth + "%";
      var y = (event.clientY * 100) / window.innerHeight + "%";
      eyes.forEach((eye) => {
        eye.style.left = x;
        eye.style.top = y;
        eye.style.transform = "translate(-" + x + ", -" + y + ")";
      });
    };
    setInterval(() => {
      slide.style.transition = "transform 0.5s ease-in-out";
      if (!isForward) {
        slide.style.transform =
          "translateX(" + -size * counter + "px) translateY(-10%)";
        if (counter <= 0) {
          isForward = true;
        } else {
          counter--;
        }
      }
      if (isForward) {
        slide.style.transform =
          "translateX(" + -size * counter + "px) translateY(-10%)";
        if (counter >= 2) {
          isForward = false;
        } else {
          counter++;
        }
      }
      // console.log(counter);
      // console.log(this.counter);
    }, 3500);
  },
};
</script>

